import React, { useState } from 'react'
import './App.css'
import axios from 'axios'

function App() {
  const [meteo, setMeteo] = useState("");

  const meteolyon = () => {
    axios.get("http://api.openweathermap.org/data/2.5/weather?q=Lyon&appid=8c2dbdca747ed150544b60e2ae60fbf1").then(
      (response) => {
        setMeteo(response.data.weather[0].description + "....." + response.data.wind.speed);
      }
    );
  };
  return (
    <div className="App">
      <span className="heading">React Meteo</span>
      <button onClick={meteolyon}>check meteo lyon</button>
      <div className="level-item has-text-centered">
      {meteo}
      </div>
    </div>
    )
}

export default App
